import { useEffect, useLayoutEffect } from "react"

const useIsomorphicLayoutEffect =
  typeof window !== "undefined" && typeof document !== "undefined"
    ? useLayoutEffect
    : useEffect

export default useIsomorphicLayoutEffect
import useIsomorphicLayoutEffect from "./useIsomorphicLayoutEffect"

const useKakaoEvent = <T extends kakao.maps.event.EventTarget>(
  /**
   * 이벤트 타겟
   */
  target: T | undefined,
  /**
   * event 타입
   */
  type: string,
  /**
   * 호출될 callback
   */
  callback: ((target: T, ...args: any[]) => void) | undefined
) => {
  useIsomorphicLayoutEffect(() => {
    if (!target || !callback) return

    const wrapCallback = (...arg: any[]) => {
      if (arg === undefined) return callback(target)
      else return callback(target, ...arg)
    }

    kakao.maps.event.addListener(target, type, wrapCallback)

    return () => {
      kakao.maps.event.removeListener(target, type, wrapCallback)
    }
  }, [target, type, callback])
}

export default useKakaoEvent
export type Libraries = ("services" | "clusterer" | "drawing")[]

export interface LoaderOptions {
  /**
   * script 객체 생성시 사용자 정의 id
   */
  id?: string
  /**
   * 발급 받은 Kakao 지도 Javscript API 키.
   *
   * @see [준비하기](https://apis.map.kakao.com/web/guide/#ready)
   */
  appkey: string
  /**
   * 사용하는 라이브러리 목록
   *
   * Kakao 지도 Javascript API 는 지도와 함께 사용할 수 있는 라이브러리 를 지원하고 있습니다.
   * 라이브러리는 javascript API와 관련되어 있지만 조금 특화된 기능을 묶어둔 것을 말합니다. 이 기능은 추가로 불러와서 사용할 수 있도록 되어있습니다.
   * 현재 사용할 수 있는 라이브러리는 다음과 같습니다.
   *
   * clusterer: 마커를 클러스터링 할 수 있는 클러스터러 라이브러리 입니다.
   * services: 장소 검색 과 주소-좌표 변환 을 할 수 있는 services 라이브러리 입니다.
   * drawing: 지도 위에 마커와 그래픽스 객체를 쉽게 그릴 수 있게 그리기 모드를 지원하는 drawing 라이브러리 입니다.
   * 라이브러리는 계속해서 추가될 예정입니다.
   */
  libraries?: Libraries
  /**
   * 사용자 정의 Kakao 지도 javascript 경로 지정
   *
   * @default "//dapi.kakao.com/v2/maps/sdk.js"
   */
  url?: string
  /**
   * 보안을 위한 nonce 값 설정
   */
  nonce?: string
  /**
   * 스크립트 로드 재시도 횟수
   */
  retries?: number
}

export enum LoaderStatus {
  INITIALIZED,
  LOADING,
  SUCCESS,
  FAILURE,
}

const DEFAULT_ID = "__reactKakaoMapsSdkloaderId"

/**
 * Kakao Map Api Loader
 *
 * `new Loader(options).load()` 함수를 이용하여 Api를 비동기적으로 삽입할 수 있습니다.
 *
 * 해당 Loader를 이용시 `react-kakao-maps-sdk` 내부에서 injection 되는 이벤트를 감지하여 kakao map api 로딩 이후에 렌더링을 진행합니다.
 */
export class Loader {
  private static instance: Loader
  private static loadcheckcallbacks: ((e?: ErrorEvent) => void)[] = []

  public readonly id: string
  public readonly appkey: string
  public readonly url: string
  public readonly libraries: Libraries
  public readonly nonce: string | undefined
  public readonly retries: number

  private callbacks: ((e?: ErrorEvent) => void)[] = []
  private done = false
  private loading = false
  private errors: ErrorEvent[] = []
  private onerrorEvent: ErrorEvent | undefined

  constructor({
    appkey,
    id = DEFAULT_ID,
    libraries = [],
    nonce,
    retries = 3,
    url = "//dapi.kakao.com/v2/maps/sdk.js",
  }: LoaderOptions) {
    this.id = id
    this.appkey = appkey
    this.libraries = libraries
    this.nonce = nonce
    this.retries = retries
    this.url = url

    if (Loader.instance) {
      if (!Loader.equalOptions(this.options, Loader.instance.options)) {
        throw new Error(
          `Loader must not be called again with different options. ${JSON.stringify(
            this.options
          )} !== ${JSON.stringify(Loader.instance.options)}`
        )
      }

      return Loader.instance
    }
    Loader.instance = this
  }

  public get options() {
    return {
      appkey: this.appkey,
      id: this.id,
      libraries: this.libraries,
      nonce: this.nonce,
      retries: this.retries,
      url: this.url,
    }
  }

  public static isLoaded(): Promise<boolean> {
    return new Promise((resolve) => {
      if (!Loader.instance) {
        if (window.kakao && window.kakao.maps) {
          /**
           * kakao 객체가 autoload=false 되어 있거나, 아직 로딩이 완료 되지 못한 상황인 경우 load 처리를 실행하거나 기다리도록 처리 필요.
           */
          // @ts-ignore
          if (window.kakao.maps.readyState !== 0) {
            return resolve(true)
          }
          return window.kakao.maps.load(() => {
            resolve(true)
          })
        }
        return Loader.loadcheckcallbacks.push((e) => {
          resolve(!e)
        })
      }

      if (
        Loader.instance.status === LoaderStatus.FAILURE ||
        Loader.instance.status === LoaderStatus.INITIALIZED
      ) {
        return resolve(false)
      }

      if (Loader.instance.status === LoaderStatus.LOADING) {
        Loader.loadcheckcallbacks.push((e) => resolve(!e))
      }

      return resolve(true)
    })
  }

  public load(): Promise<typeof kakao> {
    return new Promise((resolve, reject) => {
      this.loadCallback((err?: ErrorEvent) => {
        if (!err) {
          resolve(window.kakao)
        } else {
          reject(err.error)
        }
      })
    })
  }

  public get status(): LoaderStatus {
    if (this.onerrorEvent) {
      return LoaderStatus.FAILURE
    }
    if (this.done) {
      return LoaderStatus.SUCCESS
    }
    if (this.loading) {
      return LoaderStatus.LOADING
    }
    return LoaderStatus.INITIALIZED
  }

  private get failed(): boolean {
    return this.done && !this.loading && this.errors.length >= this.retries + 1
  }

  private loadCallback(fn: (e?: ErrorEvent) => void): void {
    this.callbacks.push(fn)
    this.execute()
  }

  private resetIfRetryingFailed(): void {
    if (this.failed) {
      this.reset()
    }
  }

  private reset(): void {
    this.deleteScript()
    this.done = false
    this.loading = false
    this.errors = []
    this.onerrorEvent = undefined
  }

  private execute() {
    this.resetIfRetryingFailed()

    if (this.done) {
      this.callback()
    } else {
      if (window.kakao && window.kakao.maps) {
        console.warn(
          "Kakao Maps이 이미 외부 요소에 의해 로딩되어 있습니다." +
            "설정한 옵션과 일치 하지 않을 수 있으며, 이에 따른 예상치 동작이 발생할 수 있습니다."
        )
        this.callback()
        return
      }

      if (!this.loading) {
        this.loading = true
        this.setScript()
      }
    }
  }

  private setScript() {
    if (document.getElementById(this.id)) {
      this.callback()
    }

    const url = this.createUrl()
    const script = document.createElement("script")

    script.id = this.id
    script.type = "text/javascript"
    script.src = url
    script.onerror = this.loadErrorCallback.bind(this)
    script.onload = this.callback.bind(this)
    script.defer = true
    script.async = true

    if (this.nonce) {
      script.nonce = this.nonce
    }

    document.head.appendChild(script)
  }

  private loadErrorCallback(
    event: Event | string,
    source?: string,
    lineno?: number,
    colno?: number,
    error?: Error
  ): void {
    this.errors.push({
      event,
      source,
      // @ts-ignore
      lineno,
      // @ts-ignore
      colno,
      error,
    })

    if (this.errors.length <= this.retries) {
      const delay = this.errors.length * 2 ** this.errors.length

      console.log(`Failed to load Kakao Maps script, retrying in ${delay} ms.`)

      setTimeout(() => {
        this.deleteScript()
        this.setScript()
      }, delay)
    } else {
      this.onerrorEvent = this.errors[this.errors.length - 1]
      this.callback()
    }
  }

  public createUrl(): string {
    let url = this.url
    url += `?appkey=${this.appkey}`

    if (this.libraries.length) {
      url += `&libraries=${this.libraries.join(",")}`
    }

    url += `&autoload=false`

    return url
  }

  private deleteScript() {
    const script = document.getElementById(this.id)
    if (script) {
      script.remove()
    }
  }

  private callback() {
    kakao.maps.load(() => {
      this.done = true
      this.loading = false

      this.callbacks.forEach((cb) => {
        cb(this.onerrorEvent)
      })
      this.callbacks = []

      Loader.loadcheckcallbacks.forEach((cb) => {
        cb(this.onerrorEvent)
      })
      Loader.loadcheckcallbacks = []
    })
  }

  private static equalOptions(
    a: typeof Loader.prototype.options,
    b: typeof Loader.prototype.options
  ): boolean {
    if (a.appkey !== b.appkey) return false
    if (a.id !== b.id) return false

    if (a.libraries.length !== b.libraries.length) return false
    for (let i = 0; i < a.libraries.length; ++i) {
      if (a.libraries[i] !== b.libraries[i]) return false
    }
    if (a.nonce !== b.nonce) return false
    if (a.retries !== b.retries) return false
    if (a.url !== b.url) return false
    return true
  }
}
import React, { useRef, useState, useImperativeHandle } from "react"
import useIsomorphicLayoutEffect from "../hooks/useIsomorphicLayoutEffect"
import useKakaoEvent from "../hooks/useKakaoEvent"
import { PolymorphicComponentPropsWithOutRef } from "../types"
import { Loader } from "../util/kakaoMapApiLoader"

export const KakaoMapContext = React.createContext<kakao.maps.Map>(
  undefined as unknown as kakao.maps.Map
)

export type MapProps = {
  /**
   * 중심으로 설정할 위치 입니다.
   */
  center:
    | {
        lat: number
        lng: number
      }
    | {
        x: number
        y: number
      }

  /**
   * 중심을 이동시킬때 Panto를 사용할지 정합니다.
   * @default false
   */
  isPanto?: boolean

  /**
   * 중심 좌표를 지정한 좌표 또는 영역으로 부드럽게 이동한다. 필요하면 확대 또는 축소도 수행한다.
   * 만약 이동할 거리가 지도 화면의 크기보다 클 경우 애니메이션 없이 이동한다.
   * padding 만큼 제외하고 영역을 계산하며, padding 을 지정하지 않으면 기본값으로 32가 사용된다.
   */
  padding?: number

  /**
   * 확대 수준 (기본값: 3)
   */
  level?: number

  /**
   * 최대 확대 수준
   */
  maxLevel?: number

  /**
   * 최소 확대 수준
   */
  minLevel?: number

  /**
   * 지도 종류 (기본값: 일반 지도)
   */
  mapTypeId?: kakao.maps.MapTypeId

  /**
   * 마우스 드래그, 휠, 모바일 터치를 이용한 시점 변경(이동, 확대, 축소) 가능 여부
   */
  draggable?: boolean

  /**
   * 마우스 휠이나 멀티터치로 지도 확대, 축소 기능을 막습니다. 상황에 따라 지도 확대, 축소 기능을 제어할 수 있습니다.
   */
  zoomable?: boolean

  /**
   * 마우스 휠, 모바일 터치를 이용한 확대 및 축소 가능 여부
   */
  scrollwheel?: boolean

  /**
   * 더블클릭 이벤트 및 더블클릭 확대 가능 여부
   */
  disableDoubleClick?: boolean

  /**
   * 더블클릭 확대 가능 여부
   */
  disableDoubleClickZoom?: boolean

  /**
   * 투영법 지정 (기본값: kakao.maps.ProjectionId.WCONG)
   */
  projectionId?: string

  /**
   * 지도 타일 애니메이션 설정 여부 (기본값: true)
   */
  tileAnimation?: boolean

  /**
   * 키보드의 방향키와 +, – 키로 지도 이동,확대,축소 가능 여부 (기본값: false)
   */
  keyboardShortcuts?:
    | boolean
    | {
        /**
         * 지도 이동 속도
         */
        speed: number
      }

  /**
   * map 생성 후 해당 객체를 전달하는 함수
   */
  onCreate?: (map: kakao.maps.Map) => void

  /**
   * 중심 좌표가 변경되면 발생한다.
   */
  onCenterChanged?: (target: kakao.maps.Map) => void

  /**
   * 확대 수준이 변경되기 직전 발생한다.
   */
  onZoomStart?: (target: kakao.maps.Map) => void

  /**
   * 확대 수준이 변경되면 발생한다.
   */
  onZoomChanged?: (target: kakao.maps.Map) => void

  /**
   * 지도 영역이 변경되면 발생한다.
   */
  onBoundsChanged?: (target: kakao.maps.Map) => void

  /**
   * 지도를 클릭하면 발생한다.
   */
  onClick?: (
    target: kakao.maps.Map,
    mouseEvent: kakao.maps.event.MouseEvent
  ) => void

  /**
   * 지도를 더블클릭하면 발생한다.
   */
  onDoubleClick?: (
    target: kakao.maps.Map,
    mouseEvent: kakao.maps.event.MouseEvent
  ) => void

  /**
   * 지도를 마우스 오른쪽 버튼으로 클릭하면 발생한다.
   */
  onRightClick?: (
    target: kakao.maps.Map,
    mouseEvent: kakao.maps.event.MouseEvent
  ) => void

  /**
   * 지도에서 마우스 커서를 이동하면 발생한다.
   */
  onMouseMove?: (
    target: kakao.maps.Map,
    mouseEvent: kakao.maps.event.MouseEvent
  ) => void

  /**
   * 드래그를 시작할 때 발생한다.
   */
  onDragStart?: (
    target: kakao.maps.Map,
    mouseEvent: kakao.maps.event.MouseEvent
  ) => void

  /**
   * 드래그를 하는 동안 발생한다.
   */
  onDrag?: (
    target: kakao.maps.Map,
    mouseEvent: kakao.maps.event.MouseEvent
  ) => void

  /**
   * 드래그가 끝날 때 발생한다.
   */
  onDragEnd?: (
    target: kakao.maps.Map,
    mouseEvent: kakao.maps.event.MouseEvent
  ) => void

  /**
   * 중심 좌표나 확대 수준이 변경되면 발생한다.
   * 단, 애니메이션 도중에는 발생하지 않는다.
   */
  onIdle?: (target: kakao.maps.Map) => void

  /**
   * 확대수준이 변경되거나 지도가 이동했을때 타일 이미지 로드가 모두 완료되면 발생한다.
   * 지도이동이 미세하기 일어나 타일 이미지 로드가 일어나지 않은경우 발생하지 않는다.
   */
  onTileLoaded?: (target: kakao.maps.Map) => void

  /**
   * 지도 기본 타일(일반지도, 스카이뷰, 하이브리드)이 변경되면 발생한다.
   */
  onMaptypeidChanged?: (target: kakao.maps.Map) => void

  children?: React.ReactNode | undefined
}

type MapComponent = <T extends React.ElementType = "div">(
  props: PolymorphicComponentPropsWithOutRef<T, MapProps>
) => React.ReactElement | null

/**
 * 기본적인 Map 객체를 생성하는 Comeponent 입니다.
 * props로 받는 `on*` 이벤트는 해당 `kakao.maps.Map` 객체를 함께 인자로 전달 합니다.
 *
 * `ref`를 통해 `map` 객체에 직접 접근하여 사용 또는 onCreate 이벤트를 이용하여 접근이 가능합니다.
 *
 * > *주의 사항* `Map`, `RoadView` 컴포넌트에 한하여, ref 객체가 컴포넌트 마운트 시점에 바로 초기화가 안될 수 있습니다.
 * >
 * > 컴포넌트 마운트 시점에 `useEffect` 를 활용하여, 특정 로직을 수행하고 싶은 경우 `ref` 객체를 사용하는 것보다
 * > `onCreate` 이벤트와 `useState`를 함께 활용하여 제어하는 것을 추천 드립니다.
 */
const Map: MapComponent = React.forwardRef(
  <T extends React.ElementType = "div">(
    {
      as,
      children,
      center,
      isPanto = false,
      padding = 32,
      disableDoubleClick,
      disableDoubleClickZoom,
      draggable,
      zoomable,
      keyboardShortcuts,
      level,
      maxLevel,
      minLevel,
      mapTypeId,
      projectionId,
      scrollwheel,
      tileAnimation,
      onBoundsChanged,
      onCenterChanged,
      onClick,
      onDoubleClick,
      onDrag,
      onDragEnd,
      onDragStart,
      onIdle,
      onMaptypeidChanged,
      onMouseMove,
      onRightClick,
      onTileLoaded,
      onZoomChanged,
      onZoomStart,
      onCreate,
      ...props
    }: PolymorphicComponentPropsWithOutRef<T, MapProps>,
    ref: React.ForwardedRef<kakao.maps.Map>
  ) => {
    const Container = as || "div"
    const [isLoaded, setIsLoaded] = useState(false)
    const [map, setMap] = useState<kakao.maps.Map>()
    const container = useRef<HTMLDivElement>(null)

    useIsomorphicLayoutEffect(() => {
      Loader.isLoaded().then(setIsLoaded)
    }, [])

    useIsomorphicLayoutEffect(() => {
      if (!isLoaded) return

      const MapContainer = container.current

      if (!MapContainer) {
        return
      }

      const initalMapCenter =
        "lat" in center
          ? new kakao.maps.LatLng(center.lat, center.lng)
          : new kakao.maps.Coords(center.x, center.y)

      const kakaoMap = new kakao.maps.Map(MapContainer, {
        center: initalMapCenter,
        disableDoubleClick: disableDoubleClick,
        disableDoubleClickZoom: disableDoubleClickZoom,
        draggable: draggable,
        keyboardShortcuts: keyboardShortcuts,
        level: level,
        mapTypeId: mapTypeId,
        projectionId: projectionId,
        scrollwheel: scrollwheel,
        tileAnimation: tileAnimation,
      })

      setMap(kakaoMap)

      return () => {
        MapContainer.innerHTML = ""
      }
    }, [
      isLoaded,
      disableDoubleClick,
      disableDoubleClickZoom,
      mapTypeId,
      tileAnimation,
    ])

    useImperativeHandle(ref, () => map!, [map])

    useIsomorphicLayoutEffect(() => {
      if (!map || !onCreate) return
      onCreate(map)
    }, [map, onCreate])

    // center position 변경시 map center 변경
    useIsomorphicLayoutEffect(() => {
      if (!map) return

      let prevCenter = map.getCenter()
      if (prevCenter instanceof kakao.maps.Coords) {
        prevCenter = prevCenter.toLatLng()
      }

      const centerPosition =
        "lat" in center
          ? new kakao.maps.LatLng(center.lat, center.lng)
          : new kakao.maps.Coords(center.x, center.y)

      if (
        (centerPosition instanceof kakao.maps.LatLng &&
          centerPosition.equals(prevCenter)) ||
        (centerPosition instanceof kakao.maps.Coords &&
          centerPosition.toLatLng().equals(prevCenter))
      ) {
        return
      }

      if (isPanto) {
        map.panTo(centerPosition, padding)
      } else {
        map.setCenter(centerPosition)
      }
      // @ts-ignore
    }, [map, center.lat, center.lng, center.x, center.y])

    useIsomorphicLayoutEffect(() => {
      if (!map || typeof draggable === "undefined") return
      map.setDraggable(draggable)
    }, [map, draggable])

    useIsomorphicLayoutEffect(() => {
      if (!map || typeof zoomable === "undefined") return
      map.setZoomable(zoomable)
    }, [map, zoomable])

    useIsomorphicLayoutEffect(() => {
      if (!map || !keyboardShortcuts || typeof keyboardShortcuts !== "boolean")
        return
      map.setKeyboardShortcuts(keyboardShortcuts)
    }, [map, keyboardShortcuts])

    useIsomorphicLayoutEffect(() => {
      if (!map || !level) return
      map.setLevel(level)
    }, [map, level])

    useIsomorphicLayoutEffect(() => {
      if (!map || !mapTypeId) return
      map.setMapTypeId(mapTypeId)
    }, [map, mapTypeId])

    useIsomorphicLayoutEffect(() => {
      if (!map || !projectionId) return
      map.setProjectionId(projectionId)
    }, [map, projectionId])

    useIsomorphicLayoutEffect(() => {
      if (!map || !maxLevel) return
      map.setMaxLevel(maxLevel)
    }, [map, maxLevel])

    useIsomorphicLayoutEffect(() => {
      if (!map || !minLevel) return
      map.setMinLevel(minLevel)
    }, [map, minLevel])

    useKakaoEvent(map, "bounds_changed", onBoundsChanged)
    useKakaoEvent(map, "center_changed", onCenterChanged)
    useKakaoEvent(map, "click", onClick)
    useKakaoEvent(map, "dblclick", onDoubleClick)
    useKakaoEvent(map, "drag", onDrag)
    useKakaoEvent(map, "dragstart", onDragStart)
    useKakaoEvent(map, "dragend", onDragEnd)
    useKakaoEvent(map, "idle", onIdle)
    useKakaoEvent(map, "maptypeid_changed", onMaptypeidChanged)
    useKakaoEvent(map, "mousemove", onMouseMove)
    useKakaoEvent(map, "rightclick", onRightClick)
    useKakaoEvent(map, "tilesloaded", onTileLoaded)
    useKakaoEvent(map, "zoom_changed", onZoomChanged)
    useKakaoEvent(map, "zoom_start", onZoomStart)

    return (
      <>
        <Container {...props} ref={container} />
        {map && (
          <KakaoMapContext.Provider value={map}>
            {children}
          </KakaoMapContext.Provider>
        )}
      </>
    )
  }
)

export default Map
import { useContext } from "react"
import { KakaoMapContext } from "../components/Map"

/**
 * kakao map 객체를 가져오는 hook 입니다.
 * Map 객체 내부가 아니라면 Error를 발생 시킵니다.
 */
const useMap = (componentName?: string) => {
  const kakaoMap = useContext(KakaoMapContext)
  if (!kakaoMap) {
    throw new Error(
      `${
        componentName ? componentName + " Component" : "useMap"
      } must exist inside Map Component!`
    )
  }

  return kakaoMap
}

export default useMap
import React, {
  useLayoutEffect,
  useImperativeHandle,
  useMemo,
  useRef,
} from "react"
import ReactDom from "react-dom"

interface InfoWindowProps {
  map: kakao.maps.Map | kakao.maps.Roadview
  position: kakao.maps.LatLng | kakao.maps.Viewpoint
  marker?: kakao.maps.Marker
  /**
   * 인포윈도우를 열 때 지도가 자동으로 패닝하지 않을지의 여부
   * @default false
   */
  disableAutoPan?: boolean

  /**
   * 삭제 가능한 인포윈도우
   */
  removable?: boolean

  /**
   * 인포윈도우 엘리먼트의 z-index 속성 값
   */
  zIndex?: number

  /**
   * 로드뷰에 올라있는 인포윈도우의 높이 값(m 단위)
   */
  altitude?: number

  /**
   * 로드뷰 상에서 인포윈도우의 가시반경(m 단위), 두 지점 사이의 거리가 지정한 값보다 멀어지면 인포윈도우는 보이지 않게 된다
   */
  range?: number

  /**
   * 인포윈도우 객체 생성후 해당 객체를 반환하는 함수
   */
  onCreate?: (infoWindow: kakao.maps.InfoWindow) => void
}

const InfoWindow = React.forwardRef<
  kakao.maps.InfoWindow,
  React.PropsWithChildren<InfoWindowProps>
>(
  (
    {
      map,
      position,
      marker,
      children,
      altitude,
      disableAutoPan,
      range,
      removable,
      zIndex,
      onCreate,
    },
    ref
  ) => {
    const container = useRef(document.createElement("div"))

    const infoWindow = useMemo(() => {
      const kakaoInfoWindow = new kakao.maps.InfoWindow({
        altitude: altitude,
        disableAutoPan: disableAutoPan,
        range: range,
        removable: removable,
        zIndex: zIndex,
        content: container.current,
        position: position,
      })
      container.current.style.display = "none"
      return kakaoInfoWindow
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [disableAutoPan, removable])

    useImperativeHandle(ref, () => infoWindow, [infoWindow])

    useLayoutEffect(() => {
      infoWindow.open(map, marker)
      return () => {
        infoWindow.close()
      }
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [map, marker])

    useLayoutEffect(() => {
      if (onCreate) onCreate(infoWindow)
    }, [infoWindow, onCreate])

    useLayoutEffect(() => {
      if (!infoWindow) return
      infoWindow.setPosition(position)
    }, [infoWindow, position])

    useLayoutEffect(() => {
      if (!infoWindow || !altitude) return
      infoWindow.setAltitude(altitude)
    }, [infoWindow, altitude])

    useLayoutEffect(() => {
      if (!infoWindow || !range) return
      infoWindow.setRange(range)
    }, [infoWindow, range])

    useLayoutEffect(() => {
      if (!infoWindow || !zIndex) return
      infoWindow.setZIndex(zIndex)
    }, [infoWindow, zIndex])

    return (
      container.current.parentElement &&
      ReactDom.createPortal(children, container.current.parentElement)
    )
  }
)

export default InfoWindow
import React, { useLayoutEffect, useImperativeHandle, useMemo } from "react"
import useKakaoEvent from "../hooks/useKakaoEvent"
import useMap from "../hooks/useMap"

export const KakaoMapMarkerClustererContext =
  React.createContext<kakao.maps.MarkerClusterer>(
    undefined as unknown as kakao.maps.MarkerClusterer
  )

export interface MarkerClustererProps {
  /**
   * 클러스터의 격자 크기. 화면 픽셀 단위이며 해당 격자 영역 안에 마커가 포함되면 클러스터에 포함시킨다
   * @default 60
   */
  gridSize?: number
  /**
   * 마커들의 좌표 평균을 클러스터 좌표 설정 여부
   * @default false
   */
  averageCenter?: boolean
  /**
   * 클러스터링 할 지도의 최소 레벨 값. 지정한 숫자에 해당하는 레벨 미만에서는 클러스터링 하지 않는다
   * @default 0
   */
  minLevel?: number
  /**
   * 클러스터링 할 최소 마커 수
   * @default 2
   */
  minClusterSize?: number
  /**
   * 클러스터의 스타일. 여러개를 선언하면 calculator 로 구분된 사이즈 구간마다 서로 다른 스타일을 적용시킬 수 있다
   */
  styles?: React.CSSProperties[] | object[]
  /**
   * 클러스터에 표시할 문자열 또는 문자열 생성 함수.
   * @default "클러스터에 포함된 숫자"
   */
  texts?: string[] | ((size: number) => string)
  /**
   * 클러스터 크기를 구분하는 값을 가진 배열 또는 구분값 생성함수
   * @default [10, 100, 1000, 10000]
   */
  calculator?: number[] | ((size: number) => number[])
  /**
   * 클러스터 클릭 시 지도 확대 여부. true로 설정하면 클러스터 클릭 시 확대 되지 않는다
   * @default false
   */
  disableClickZoom?: boolean
  /**
   * 클러스터 클릭 가능 여부 지정 옵션. false일 경우 클러스터의 clusterclick, clusterdblclick, clusterrightclick 이벤트가 발생하지 않으며, 커서가 변경되지 않는다.
   * @default true
   */
  clickable?: boolean
  /**
   * 클러스터에 마우스 over/out 가능 여부 지정 옵션. false일 경우 클러스터의 clusterover, clusterout 이벤트가 발생하지 않는다.
   * @default true
   */
  hoverable?: boolean

  /**
   * 클러스터 마커를 클릭 했을 때 발생한다.
   * 이벤트 핸들러 함수 인자로는 Cluster 객체가 넘어온다.
   * 클러스터 마커 클릭 시 지도가 줌인 되는 경우 원하는 Cluster 객체를 얻지 못할 수도 있다.
   * 때문에 MarkerClusterer 를 생성할 때 disableClickZoom 옵션을 true로 설정하여
   * 클러스터 마커를 클릭했을 때 지도가 줌인되지 않도록 설정 후 사용한다.
   */
  onClusterclick?: (
    target: kakao.maps.MarkerClusterer,
    cluster: kakao.maps.Cluster
  ) => void
  /**
   * 클러스터 마커를 마우스 오버 했을 때 발생한다
   * 이벤트 핸들러 함수 인자로는 마우스 오버한 Cluster 객체가 넘어온다.
   */
  onClusterover?: (
    target: kakao.maps.MarkerClusterer,
    cluster: kakao.maps.Cluster
  ) => void
  /**
   * 클러스터 마커를 마우스 아웃 했을 때 발생한다
   * 이벤트 핸들러 함수 인자로는 마우스 아웃된 Cluster 객체가 넘어온다.
   */
  onClusterout?: (
    target: kakao.maps.MarkerClusterer,
    cluster: kakao.maps.Cluster
  ) => void
  /**
   * 클러스터 마커를 더블클릭 했을 때 발생한다
   * 이벤트 핸들러 함수 인자로는 더블클릭한 Cluster 객체가 넘어온다.
   * MarkerClusterer 를 생성할 때 disableClickZoom 옵션을 true로 설정해야만 이벤트가 발생한다.
   */
  onClusterdblclick?: (
    target: kakao.maps.MarkerClusterer,
    cluster: kakao.maps.Cluster
  ) => void
  /**
   * 클러스터 마커를 오른쪽 클릭 했을 때 발생한다
   * 이벤트 핸들러 함수 인자로는 오른쪽 클릭한 Cluster 객체가 넘어온다.
   */
  onClusterrightclick?: (
    target: kakao.maps.MarkerClusterer,
    cluster: kakao.maps.Cluster
  ) => void
  /**
   * 클러스터링이 완료됐을 때 발생한다.
   * 이벤트 핸들러 함수 인자로는 생성된 Cluster 객체 전체가 배열로 넘어온다.
   */
  onClustered?: (
    target: kakao.maps.MarkerClusterer,
    clusters: kakao.maps.Cluster[]
  ) => void
  /**
   * MarkerClusterer 생성 후 해당 객체를 전달하는 함수
   */
  onCreate?: (target: kakao.maps.MarkerClusterer) => void
}

const MarkerClusterer = React.forwardRef<
  kakao.maps.MarkerClusterer,
  React.PropsWithChildren<MarkerClustererProps>
>(
  (
    {
      children,
      averageCenter,
      calculator,
      clickable,
      disableClickZoom,
      gridSize,
      hoverable,
      minClusterSize,
      minLevel,
      styles,
      texts,
      onClusterclick,
      onClusterdblclick,
      onClustered,
      onClusterout,
      onClusterover,
      onClusterrightclick,
      onCreate,
    },
    ref
  ) => {
    const map = useMap(`MarkerClusterer`)
    const markerCluster = useMemo(() => {
      if (!window.kakao.maps.MarkerClusterer) {
        console.warn(
          "clusterer 라이브러리를 별도 로드 해야 사용 가능합니다. `https://apis.map.kakao.com/web/guide/#loadlibrary`"
        )
        return
      }
      return new kakao.maps.MarkerClusterer({
        averageCenter,
        calculator,
        clickable,
        disableClickZoom,
        gridSize,
        hoverable,
        minClusterSize,
        minLevel,
        styles,
        texts,
      })
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    useImperativeHandle(ref, () => markerCluster!, [markerCluster])

    useLayoutEffect(() => {
      markerCluster?.setMap(map)
    }, [map, markerCluster])

    useLayoutEffect(() => {
      if (markerCluster && onCreate) onCreate(markerCluster)
    }, [markerCluster, onCreate])

    useLayoutEffect(() => {
      if (markerCluster && gridSize) {
        markerCluster.setGridSize(gridSize)
        markerCluster.redraw()
      }
    }, [markerCluster, gridSize])

    useLayoutEffect(() => {
      if (markerCluster && minClusterSize) {
        markerCluster.setMinClusterSize(minClusterSize)
        markerCluster.redraw()
      }
    }, [markerCluster, minClusterSize])

    useLayoutEffect(() => {
      if (markerCluster && typeof averageCenter !== "undefined") {
        markerCluster.setAverageCenter(averageCenter)
        markerCluster.redraw()
      }
    }, [markerCluster, averageCenter])

    useLayoutEffect(() => {
      if (markerCluster && minLevel) {
        markerCluster.setMinLevel(minLevel)
        markerCluster.redraw()
      }
    }, [markerCluster, minLevel])

    useLayoutEffect(() => {
      if (markerCluster && texts) {
        markerCluster.setTexts(texts)
        markerCluster.redraw()
      }
    }, [markerCluster, texts])

    useLayoutEffect(() => {
      if (markerCluster && calculator) {
        markerCluster.setCalculator(calculator)
        markerCluster.redraw()
      }
    }, [markerCluster, calculator])

    useLayoutEffect(() => {
      if (markerCluster && styles) {
        markerCluster.setStyles(styles)
        markerCluster.redraw()
      }
    }, [markerCluster, styles])

    useKakaoEvent(markerCluster, "clusterclick", onClusterclick)
    useKakaoEvent(markerCluster, "clusterover", onClusterover)
    useKakaoEvent(markerCluster, "clusterout", onClusterout)
    useKakaoEvent(markerCluster, "clusterdblclick", onClusterdblclick)
    useKakaoEvent(markerCluster, "clusterrightclick", onClusterrightclick)
    useKakaoEvent(markerCluster, "clustered", onClustered)

    if (!markerCluster) {
      return null
    }

    return (
      <KakaoMapMarkerClustererContext.Provider value={markerCluster}>
        {children}
      </KakaoMapMarkerClustererContext.Provider>
    )
  }
)

export default MarkerClusterer
import React, {
  useContext,
  useImperativeHandle,
  useLayoutEffect,
  useMemo,
} from "react"
import useKakaoEvent from "../hooks/useKakaoEvent"
import InfoWindow from "./InfoWindow"
import { KakaoMapMarkerClustererContext } from "./MarkerClusterer"

interface MarkerProps {
  map: kakao.maps.Map | kakao.maps.Roadview
  position: kakao.maps.LatLng | kakao.maps.Viewpoint

  /**
   * marker 생성 후 marker 객체를 전달하는 callback
   */
  onCreate?: (marker: kakao.maps.Marker) => void

  /**
   * click 이벤트 핸들러
   */
  onClick?: (marker: kakao.maps.Marker) => void

  /**
   * mouseover 이벤트 핸들러
   */
  onMouseOver?: (marker: kakao.maps.Marker) => void

  /**
   * mouseout 이벤트 핸들러
   */
  onMouseOut?: (marker: kakao.maps.Marker) => void

  /**
   * dragstart 이벤트 핸들러
   */
  onDragStart?: (marker: kakao.maps.Marker) => void

  /**
   * dragend 이벤트 핸들러
   */
  onDragEnd?: (marker: kakao.maps.Marker) => void

  /**
   * 마커의 이미지
   */
  image?: kakao.maps.MarkerImage

  /**
   * 마커 엘리먼트의 타이틀 속성 값 (툴팁)
   */
  title?: string

  /**
   * 드래그 가능한 마커, 로드뷰에 올릴 경우에는 유효하지 않다.
   */
  draggable?: boolean

  /**
   * 클릭 가능한 마커
   */
  clickable?: boolean

  /**
   * 마커 엘리먼트의 z-index 속성 값
   */
  zIndex?: number

  /**
   * 마커 투명도 (0-1)
   */
  opacity?: number

  /**
   * 로드뷰에 올라있는 마커의 높이 값(m 단위)
   */
  altitude?: number

  /**
   * 로드뷰 상에서 마커의 가시반경(m 단위), 두 지점 사이의 거리가 지정한 값보다 멀어지면 마커는 로드뷰에서 보이지 않게 된다.
   */
  range?: number

  /**
   * InfoWindow 옵션
   */
  infoWindowOptions?: {
    /**
     * 인포윈도우를 열 때 지도가 자동으로 패닝하지 않을지의 여부 (기본값: false)
     */
    disableAutoPan?: boolean

    /**
     * 삭제 가능한 인포윈도우
     */
    removable?: boolean

    /**
     * 인포윈도우 엘리먼트의 z-index 속성 값
     */
    zIndex?: number

    /**
     * 로드뷰에 올라있는 인포윈도우의 높이 값(m 단위)
     */
    altitude?: number

    /**
     * 로드뷰 상에서 인포윈도우의 가시반경(m 단위), 두 지점 사이의 거리가 지정한 값보다 멀어지면 인포윈도우는 보이지 않게 된다
     */
    range?: number
  }
}

const Marker = React.forwardRef<
  kakao.maps.Marker,
  React.PropsWithChildren<MarkerProps>
>(
  (
    {
      map,
      position,
      children,
      altitude,
      clickable,
      draggable,
      image,
      infoWindowOptions,
      onCreate,
      onClick,
      onDragEnd,
      onDragStart,
      onMouseOut,
      onMouseOver,
      opacity,
      range,
      title,
      zIndex,
    },
    ref
  ) => {
    const markerCluster = useContext(KakaoMapMarkerClustererContext)

    // Marker 객체는 단 한번만 생성 되도록 함
    const marker = useMemo(() => {
      const kakaoMarker = new kakao.maps.Marker({
        altitude,
        clickable,
        draggable,
        image,
        opacity,
        range,
        title,
        zIndex,
        position,
      })

      return kakaoMarker
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    useImperativeHandle(ref, () => marker, [marker])

    useLayoutEffect(() => {
      if (markerCluster) {
        markerCluster.addMarker(marker)
      } else {
        marker.setMap(map)
      }

      return () => {
        if (markerCluster) {
          markerCluster.removeMarker(marker)
        } else {
          marker.setMap(null)
        }
      }
    }, [map, markerCluster, marker])

    useLayoutEffect(() => {
      if (onCreate) onCreate(marker)
    }, [marker, onCreate])

    useKakaoEvent(marker, "click", onClick)
    useKakaoEvent(marker, "dragstart", onDragStart)
    useKakaoEvent(marker, "dragend", onDragEnd)
    useKakaoEvent(marker, "mouseout", onMouseOut)
    useKakaoEvent(marker, "mouseover", onMouseOver)

    // position이 변경되면 객체를 갱신한다.
    useLayoutEffect(() => {
      if (!map || !marker || !position) return

      marker.setPosition(position)
    }, [map, marker, position])

    // image 객체가 존재하면 이미지를 로드한다
    useLayoutEffect(() => {
      if (!map || !marker || !image) return

      marker.setImage(image)
    }, [map, marker, image])

    // altitude 값이 있으면 높이를 조정한다
    useLayoutEffect(() => {
      if (!map || !marker || !altitude) return

      marker.setAltitude(altitude)
    }, [map, marker, altitude])

    // clickable 값이 있으면 클릭이 가능하도록 한다.
    useLayoutEffect(() => {
      if (!map || !marker || typeof clickable === "undefined") return

      marker.setClickable(clickable)
    }, [map, marker, clickable])

    // draggable 값이 있으면 드래그가 가능하도록 한다.
    useLayoutEffect(() => {
      if (!map || !marker || typeof draggable === "undefined") return

      marker.setDraggable(draggable)
    }, [map, marker, draggable])

    // opacity 값이 있으면 투명도를 조절한다.
    useLayoutEffect(() => {
      if (!map || !marker || !opacity) return

      marker.setOpacity(opacity)
    }, [map, marker, opacity])

    // range 값이 있으면 마커의 가시반경을 조절한다.
    useLayoutEffect(() => {
      if (!map || !marker || !range) return

      marker.setRange(range)
    }, [map, marker, range])

    // title 값이 있으면 마커의 제목을 조절한다.
    useLayoutEffect(() => {
      if (!map || !marker || !title) return

      marker.setTitle(title)
    }, [map, marker, title])

    // zIndex 값이 있으면 마커의 zindex를 조절한다.
    useLayoutEffect(() => {
      if (!map || !marker || !zIndex) return

      marker.setZIndex(zIndex)
    }, [map, marker, zIndex])

    if (children)
      return (
        <InfoWindow
          position={position}
          map={map}
          marker={marker}
          altitude={infoWindowOptions?.altitude}
          disableAutoPan={infoWindowOptions?.disableAutoPan}
          range={infoWindowOptions?.range}
          removable={infoWindowOptions?.removable}
          zIndex={infoWindowOptions?.zIndex}
        >
          {children}
        </InfoWindow>
      )

    return null
  }
)

export default Marker
import React, { useMemo } from "react"
import useMap from "../hooks/useMap"
import Marker from "./Marker"

export interface MapMarkerProps {
  /**
   * 표시 위치
   */
  position:
    | {
        lat: number
        lng: number
      }
    | {
        x: number
        y: number
      }

  image?: {
    /**
     * 표시 이미지 src
     */
    src: string

    /**
     * 표시 이미지 크기
     */
    size: {
      width: number
      height: number
    }

    options?: {
      /**
       * 마커 이미지의 alt 속성값을 정의한다.
       */
      alt?: string

      /**
       * 마커의 클릭 또는 마우스오버 가능한 영역을 표현하는 좌표값
       */
      coords?: string

      /**
       * 마커의 좌표에 일치시킬 이미지 안의 좌표 (기본값: 이미지의 가운데 아래)
       */
      offset?: { x: number; y: number }

      /**
       * 마커의 클릭 또는 마우스오버 가능한 영역의 모양
       */
      shape?: "default" | "rect" | "circle" | "poly"

      /**
       * 스프라이트 이미지 중 사용할 영역의 좌상단 좌표
       */
      spriteOrigin?: { x: number; y: number }

      /**
       * 스프라이트 이미지의 전체 크기
       */
      spriteSize?: { width: number; height: number }
    }
  }

  /**
   * click 이벤트 핸들러
   */
  onClick?: (marker: kakao.maps.Marker) => void

  /**
   * mouseover 이벤트 핸들러
   */
  onMouseOver?: (marker: kakao.maps.Marker) => void

  /**
   * mouseout 이벤트 핸들러
   */
  onMouseOut?: (marker: kakao.maps.Marker) => void

  /**
   * dragstart 이벤트 핸들러
   */
  onDragStart?: (marker: kakao.maps.Marker) => void

  /**
   * dragend 이벤트 핸들러
   */
  onDragEnd?: (marker: kakao.maps.Marker) => void

  /**
   * Maker 생성 이벤트 핸들러
   */
  onCreate?: (maker: kakao.maps.Marker) => void

  /**
   * 마커 엘리먼트의 타이틀 속성 값 (툴팁)
   */
  title?: string

  /**
   * 드래그 가능한 마커, 로드뷰에 올릴 경우에는 유효하지 않다.
   */
  draggable?: boolean

  /**
   * 클릭 가능한 마커
   */
  clickable?: boolean

  /**
   * 마커 엘리먼트의 z-index 속성 값
   */
  zIndex?: number

  /**
   * 마커 투명도 (0-1)
   */
  opacity?: number

  /**
   * InfoWindow 옵션
   */
  infoWindowOptions?: {
    /**
     * 인포윈도우를 열 때 지도가 자동으로 패닝하지 않을지의 여부 (기본값: false)
     */
    disableAutoPan?: boolean

    /**
     * 삭제 가능한 인포윈도우
     */
    removable?: boolean

    /**
     * 인포윈도우 엘리먼트의 z-index 속성 값
     */
    zIndex?: number
  }
}

/**
 * Map에서 Marker를 생성할 때 사용 합니다.
 * `onCreate` 이벤트를 통해 생성 후 `maker` 객체에 직접 접근하여 초기 설정이 가능합니다.
 */
const MapMarker = React.forwardRef<
  kakao.maps.Marker,
  React.PropsWithChildren<MapMarkerProps>
>(
  (
    {
      image,
      position,
      children,
      clickable,
      draggable,
      infoWindowOptions,
      onClick,
      onDragEnd,
      onDragStart,
      onMouseOut,
      onMouseOver,
      onCreate,
      opacity,
      title,
      zIndex,
    },
    ref
  ) => {
    const map = useMap(`MapMarker`)

    const markerImage = useMemo(() => {
      return (
        image &&
        new kakao.maps.MarkerImage(
          image.src,
          new kakao.maps.Size(image.size.width, image.size.height),
          {
            alt: image.options?.alt,
            coords: image.options?.coords,
            offset:
              image.options?.offset &&
              new kakao.maps.Point(
                image.options?.offset.x,
                image.options?.offset.y
              ),
            shape: image.options?.shape,
            spriteOrigin:
              image.options?.spriteOrigin &&
              new kakao.maps.Point(
                image.options?.spriteOrigin.x,
                image.options?.spriteOrigin.y
              ),
            spriteSize:
              image.options?.spriteSize &&
              new kakao.maps.Size(
                image.options?.spriteSize.width,
                image.options?.spriteSize.height
              ),
          }
        )
      )
    }, [image])

    const markerPosition = useMemo(() => {
      if ("lat" in position) {
        return new kakao.maps.LatLng(position.lat, position.lng)
      }
      return new kakao.maps.Coords(position.x, position.y).toLatLng()

      // @ts-ignore
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [position.lat, position.lng, position.x, position.y])

    return (
      <Marker
        map={map}
        position={markerPosition}
        image={markerImage}
        clickable={clickable}
        draggable={draggable}
        infoWindowOptions={infoWindowOptions}
        onClick={onClick}
        onDragEnd={onDragEnd}
        onDragStart={onDragStart}
        onMouseOut={onMouseOut}
        onMouseOver={onMouseOver}
        onCreate={onCreate}
        opacity={opacity}
        title={title}
        zIndex={zIndex}
        ref={ref}
      >
        {children}
      </Marker>
    )
  }
)

export default MapMarker
