   * var width = node.current.offsetWidth
   * var height = node.current.offsetHeight
   *
   * console.log(point, width, height)
   *
   * node.current.style.left = point.x - width / 2 + "px"
   * node.current.style.top = point.y - height / 2 + "px"
   * ```
   */
  onAdd: () => void

  /**
   * 지도에서 제거할 때 호출된다.
   * setMap(null) 을 실행하면 이 함수가 실행된다.
   * 이 함수 내에서 MapPanels에 자식 노드로 넣어준 엘리먼트를 제거하도록 한다.
   *
   * ```tsx
   * this.node.parentNode.removeChild(this.node);
   * ```
   */
  onRemove: () => void

  /**
   * 생성된 kakao.maps.AbstractOverlay 객체를 받을 수 있는 CallBack 함수 입니다.
   */
  onCreate?: (target: kakao.maps.AbstractOverlay) => void
}

/**
 * `API` 사용자가 직접 정의할 수 있는 오버레이.
 * AbstractOverlay를 상속 받아 사용할 객체를 생성하고 prototype을 연결해 준다.
 * 온전히 동작하는 오버레이를 만들고자 한다면 3가지 인터페이스 메소드( onAdd, draw, onRemove )를 구현해야 한다.
 *
 * 해당 Component 내부에서 `ReactAbstractOveraly` class를 만들어 `onAdd`, `draw`, `onRemove` 를 연결한 후 React Life Cycle에 맞춰 `setMap` 를 자동으로 호출 합니다.
 *
 * *경고* `draw`, `onAdd`, `onRemove` 의 경우 반드시 익명함수가 **아닌** 형태로 만들어서 전달해야 합니다.
 * `this.~` 를 통한 접근이 가능하니 해당 부분을 이용해서 구현 해주세요!
 *
 * ### Example Code
 *
 * ```jsx live
 * function () {
 *  const TooltipMarker = ({
 *    position,
 *  }) => {
 *    const node = useRef(document.createElement("div"))
 *
 *    const positionLatlng = useMemo(() => {
 *      return new kakao.maps.LatLng(position.lat, position.lng)
 *    }, [position.lat, position.lng])
 *
 *    function onAdd() {
 *      const panel = this.getPanels()
 *        .overlayLayer
 *      panel.appendChild(node.current)
 *    }
 *
 *    function onRemove() {
 *      node.current.parentNode.removeChild(node.current)
 *    }
 *
 *    function draw() {
 *      var projection = this.getProjection()
 *      var point = projection.pointFromCoords(positionLatlng)
 *      var width = node.current.offsetWidth
 *      var height = node.current.offsetHeight
 *
 *      console.log(point, width, height)
 *
 *      node.current.style.left = point.x - width / 2 + "px"
 *      node.current.style.top = point.y - height / 2 + "px"
 *    }
 *
 *    useEffect(() => {
 *      node.current.style.position = "absolute"
 *      node.current.style.whiteSpace = "nowrap"
 *    }, [])
 *
 *    return (
 *      <>
 *        <AbstractOverlay onAdd={onAdd} onRemove={onRemove} draw={draw} />
 *        {ReactDOM.createPortal(
 *          <div
 *            style={{
 *              color: "#000",
 *              backgroundColor: "#fff",
 *              padding: "5px",
 *              borderRadius: "15px",
 *            }}
 *          >
 *            Hello Marker!
 *          </div>,
 *          node.current
 *        )}
 *      </>
 *    )
 *  }
 *
 *  return (
 *    <>
 *      <Map // 지도를 표시할 Container
 *        center={{
 *          // 지도의 중심좌표
 *          lat: 37.54699,
 *          lng: 127.09598,
 *        }}
 *        style={{
 *          // 지도의 크기
 *          width: "100%",
 *          height: "450px",
 *        }}
 *        level={3} // 지도의 확대 레벨
 *      >
 *        <TooltipMarker
 *          position={{
 *            lat: 37.54699,
 *            lng: 127.09598,
 *          }}
 *        />
 *      </Map>
 *    </>
 *  )
 *}
 * ```
 */
const AbstractOverlay = React.forwardRef<
  kakao.maps.AbstractOverlay,
  AbstractOverlayProps
>(({ draw, onAdd, onRemove, onCreate }, ref) => {
  const map = useMap()

  const reactAbstractOverlay = useMemo(() => {
    class ReactAbstractOveraly extends kakao.maps.AbstractOverlay {
      constructor(draw: () => void, onAdd: () => void, onRemove: () => void) {
        super()
        this.draw = draw
        this.onAdd = onAdd
        this.onRemove = onRemove
      }
    }
    const overlay = new ReactAbstractOveraly(draw, onAdd, onRemove)
    return overlay
  }, [draw, onAdd, onRemove])

  useImperativeHandle(ref, () => reactAbstractOverlay, [reactAbstractOverlay])

  useLayoutEffect(() => {
    reactAbstractOverlay.setMap(map)

    return () => {
      reactAbstractOverlay.setMap(null)
    }
  }, [map, reactAbstractOverlay])

  useLayoutEffect(() => {
    if (onCreate) onCreate(reactAbstractOverlay)
  }, [reactAbstractOverlay, onCreate])

  return null
})

export default AbstractOverlay
import React, { useImperativeHandle, useRef, useState } from "react"
import useIsomorphicLayoutEffect from "../hooks/useIsomorphicLayoutEffect"
import useKakaoEvent from "../hooks/useKakaoEvent"
import { PolymorphicComponentProps, PolymorphicComponentPropsWithOutRef } from "../types"
import { Loader } from "../util/kakaoMapApiLoader"

export const KakaoRoadviewContext = React.createContext<kakao.maps.Roadview>(
  undefined as unknown as kakao.maps.Roadview
)

export interface RoadviewProps {
  /**
   * 중심으로 설정할 위치 입니다.
   * 해당 lat와 lng에 해당하는 radius범위 안에서 가장가까운 Roadview을 선택합니다.
   * 만약 없다면 lat, lng로 설정 됩니다.
   */
  position: {
    lat: number
    lng: number
    radius: number
  }

  /**
   * 로드뷰 시작 지역의 고유 아이디 값.
   */
  panoId?: number

  /**
   * panoId가 유효하지 않을 경우 지도좌표를 기반으로 데이터를 요청할 수평 좌표값.
   */
  panoX?: number

  /**
   * panoId가 유효하지 않을 경우 지도좌표를 기반으로 데이터를 요청할 수직 좌표값.
   */
  panoY?: number

  /**
   * 로드뷰 처음 실행시에 바라봐야 할 수평 각. 0이 정북방향. (0_360)
   */
  pan?: number

  /**
   * 로드뷰 처음 실행시에 바라봐야 할 수직 각.(-90_90)
   */
  tilt?: number

  /**
   * 로드뷰 줌 초기값.(-3_3)
   */
  zoom?: number

  /**
   * 로드뷰 생성후 해당 객체를 전달하는 callback.
   */
  onCreate?: (roadview: kakao.maps.Roadview) => void

  /**
   * 로드뷰가 초기화를 끝내면 발생한다.
   */
  onInit?: (target: kakao.maps.Roadview) => void

  /**
   * 파노라마 ID가 바뀌면 발생한다.
   */
  onPanoidChange?: (target: kakao.maps.Roadview) => void

  /**
   * 시점이 바뀌면 발생한다.
   */
  onViewpointChange?: (target: kakao.maps.Roadview) => void

  /**
   * 지도 좌표가 바뀌면 발생한다.
   */
  onPositionChanged?: (target: kakao.maps.Roadview) => void

  /**
   * getNearestPanoId 동작 실패시 호출 합니다.
   */
  onErrorGetNearestPanoId?: (target: kakao.maps.Roadview) => void
}

type RoadviewComponent = <T extends React.ElementType = "div">(
  props: PolymorphicComponentPropsWithOutRef<T, RoadviewProps>
) => React.ReactElement | null
/**
 * Roadview를 Roadview를 생성하는 컴포넌트 입니다.
 * props로 받는 `on*` 이벤트는 해당 `kakao.maps.Map` 객체를 반환 합니다.
 * `onCreate` 이벤트를 통해 생성 후 `Roadview` 객체에 직접 접근하여 초기 설정이 가능합니다.
 */
const Roadview: RoadviewComponent = React.forwardRef(
  <T extends React.ElementType = "div">(
    {
      as,
      children,
      position,
      pan,
      panoId,
      panoX,
      panoY,
      tilt,
      zoom,
      onCreate,
      onInit,
      onPanoidChange,
      onPositionChanged,
      onViewpointChange,
      onErrorGetNearestPanoId,
      ...props
    }: PolymorphicComponentProps<T, React.PropsWithChildren<RoadviewProps>>,
    ref: React.ForwardedRef<kakao.maps.Roadview>
  ) => {
    const Container = as || "div"
    const [isLoaded, setIsLoaded] = useState(false)
    const [isLoading, setIsLoading] = useState(true)
    const [roadview, setRoadview] = useState<kakao.maps.Roadview>()
    const container = useRef<HTMLDivElement>(null)

    useIsomorphicLayoutEffect(() => {
      Loader.isLoaded().then(setIsLoaded)
    }, [])

    useIsomorphicLayoutEffect(() => {
      if (!isLoaded) return

      const RoadviewContainer = container.current

      if (!RoadviewContainer) return

      const kakaoRoadview = new kakao.maps.Roadview(RoadviewContainer, {
        pan: pan,
        panoId: panoId,
        panoX: panoX,
        panoY: panoY,
        tilt: tilt,
        zoom: zoom,
      })

      setRoadview(kakaoRoadview)

      return () => {
        RoadviewContainer.innerHTML = ""
      }
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [isLoaded, panoX, panoY, zoom])

    useImperativeHandle(ref, () => roadview!, [roadview])

    useIsomorphicLayoutEffect(() => {
      if (!roadview || !onCreate) return
      onCreate(roadview)
    }, [roadview, onCreate])

    useIsomorphicLayoutEffect(() => {
      if (
        !roadview ||
        panoId ||
        (roadview.getPosition().getLat() === position.lat &&
          roadview.getPosition().getLng() === position.lng)
      )
        return

      const newPostion = new kakao.maps.LatLng(position.lat, position.lng)

      new kakao.maps.RoadviewClient().getNearestPanoId(
        newPostion,
        position.radius,
        (panoId) => {
          if (panoId === null && onErrorGetNearestPanoId) {
            onErrorGetNearestPanoId(roadview)
          } else {
            roadview.setPanoId(panoId, newPostion)
          }
        }
      )
    }, [
      roadview,
      panoId,
      position.lat,
      position.lng,
      position.radius,
      onErrorGetNearestPanoId,
    ])

    useIsomorphicLayoutEffect(() => {
      if (
        !roadview ||
        !panoId ||
        panoId === roadview.getPanoId() ||
        (roadview.getPosition().getLat() === position.lat &&
          roadview.getPosition().getLng() === position.lng)
      )
        return

      const newPostion = new kakao.maps.LatLng(position.lat, position.lng)
      roadview.setPanoId(panoId, newPostion)
    }, [roadview, panoId, position.lat, position.lng])

    useIsomorphicLayoutEffect(() => {
      if (!roadview) return

      const prevViewpoint = roadview.getViewpoint()

      if (prevViewpoint.pan === pan && prevViewpoint.tilt === tilt) return

      if (pan) prevViewpoint.pan = pan
      if (tilt) prevViewpoint.tilt = tilt
      roadview.setViewpoint(prevViewpoint)
    }, [roadview, pan, tilt])

    useKakaoEvent(roadview, "init", (target) => {
      setIsLoading(false)
      if (onInit) onInit(target)
    })
    useKakaoEvent(roadview, "panoid_changed", onPanoidChange)
    useKakaoEvent(roadview, "viewpoint_changed", onViewpointChange)
    useKakaoEvent(roadview, "position_changed", onPositionChanged)

    return (
      <>
        <Container ref={container} {...props} />
        {roadview && !isLoading && (
          <KakaoRoadviewContext.Provider value={roadview}>
            {children}
          </KakaoRoadviewContext.Provider>
        )}
      </>
    )
  }
)

export default Roadview
import { useContext } from "react"
import { KakaoRoadviewContext } from "../components/Roadview"

/**
 * kakao roadview 객체를 가져오는 hook 입니다.
 * Roadview 객체 내부가 아니라면 Error를 발생 시킵니다.
 */
const useRoadview = (componentName?: string) => {
  const kakaoRoadview = useContext(KakaoRoadviewContext)
  if (!kakaoRoadview) {
    throw new Error(
      `${
        componentName ? componentName + " Component" : "useRoadview"
      } must exist inside Roadview Component!`
    )
  }

  return kakaoRoadview
}

export default useRoadview
import React, {
  useImperativeHandle,
  useLayoutEffect,
  useMemo,
  useRef,
} from "react"
import ReactDOM from "react-dom"
import useRoadview from "../hooks/useRoadview"

export interface CustomOverlayRoadviewProps {
  /**
   * 커스텀 오버레이의 좌표
   */
  position:
    | {
        lat: number
        lng: number
      }
    | {
        /**
         * 가로 각도, 0부터 360 사이의 값으로 북쪽부터 시계방향으로 대응한다.
         */
        pan: number
        /**
         * 세로 각도, -90부터 90 사이의 값으로 위쪽부터 아래쪽으로 대응한다.
         */
        tilt: number
        /**
         * 확대 수준, -3부터 3 사이의 정수이다.
         */
        zoom?: number
        /**
         * 특정 위치의 로드뷰 고유의 아이디 값
         */
        panoId?: number
      }
  /**
   * true 로 설정하면 컨텐츠 영역을 클릭했을 경우 지도 이벤트를 막아준다.
   */
  clickable?: boolean

  /**
   * 컨텐츠의 x축 위치. 0_1 사이의 값을 가진다. 기본값은 0.5
   */
  xAnchor?: number

  /**
   * 컨텐츠의 y축 위치. 0_1 사이의 값을 가진다. 기본값은 0.5
   */
  yAnchor?: number

  /**
   * 커스텀 오버레이의 z-index
   */
  zIndex?: number

  /**
   * 로드뷰상에서 커스텀 오버레이의 높이(위치)를 지정한다.
   * 단위는 m(미터)이며 현재 로드뷰의 바닥 높이를 기준으로 떨어져있는 높이를 말한다.
   */
  altitude?: number

  /**
   * 커스텀 오버레이의 가시반경을 설정한다.
   * 로드뷰의 위치와 커스텀 오버레이의 위치 사이의 거리가 가시반경 이내일 경우에만 로드뷰상에 노출된다.
   * 단위는 m(미터)이며 기본값은 500m이다.
   */
  range?: number

  /**
   * 커스텀 오버레이를 생성 후 해당 객체를 가지고 callback 함수를 실행 시켜줌
   */
  onCreate?: (customOverlay: kakao.maps.CustomOverlay) => void
}

/**
 * Roadview에 CustomOverlay를 올릴 때 사용하는 컴포넌트 입니다.
 * `onCreate` 이벤트 또는 `ref` 객체를 통해서 `CustomOverlay` 객체에 직접 접근 및 초기 설정 작업을 지정할 수 있습니다.
 */
const CustomOverlayRoadview = React.forwardRef<
  kakao.maps.CustomOverlay,
  React.PropsWithChildren<CustomOverlayRoadviewProps>
>(
  (
    {
      position,
      children,
      clickable,
      xAnchor,
      yAnchor,
      zIndex,
      altitude,
      range,
      onCreate,
    },
    ref
  ) => {
    const roadview = useRoadview(`CustomOverlayRoadview`)
    const container = useRef(document.createElement("div"))

    const overlayPosition = useMemo(() => {
      if ("lat" in position) {
        return new kakao.maps.LatLng(position.lat, position.lng)
      }
      return new kakao.maps.Viewpoint(
        position.pan,
        position.tilt,
        position.zoom,
        position.panoId
      )
      /* eslint-disable react-hooks/exhaustive-deps */
    }, [
      // @ts-ignore
      position.lat,
      // @ts-ignore
      position.lng,
      // @ts-ignore
      position.pan,
      // @ts-ignore
      position.tilt,
      // @ts-ignore
      position.zoom,
      // @ts-ignore
      position.panoId,
    ])
    /* eslint-enable react-hooks/exhaustive-deps */

    const overlay = useMemo(() => {
      const KakaoCustomOverlay = new kakao.maps.CustomOverlay({
        clickable: clickable,
        xAnchor: xAnchor,
        yAnchor: yAnchor,
        zIndex: zIndex,
        position: overlayPosition,
        content: container.current,
      })
      container.current.style.display = "none"
      return KakaoCustomOverlay
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [clickable, xAnchor, yAnchor])

    useImperativeHandle(ref, () => overlay, [overlay])

    useLayoutEffect(() => {
      if (!roadview) return

      overlay.setMap(roadview)

      return () => {
        overlay.setMap(null)
      }
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [overlay, roadview])

    useLayoutEffect(() => {
      if (onCreate) onCreate(overlay)
    }, [overlay, onCreate])

    useLayoutEffect(() => {
      overlay.setPosition(overlayPosition)
    }, [overlay, overlayPosition])

    useLayoutEffect(() => {
      if (!zIndex) return
      overlay.setZIndex(zIndex)
    }, [overlay, zIndex])

    useLayoutEffect(() => {
      if (!altitude) return
      overlay.setAltitude(altitude)
    }, [overlay, altitude])

    useLayoutEffect(() => {
      if (!range) return
      overlay.setRange(range)
    }, [overlay, range])

    return (
      container.current.parentElement &&
      ReactDOM.createPortal(children, container.current.parentElement)
    )
  }
)

