   * 마커 엘리먼트의 z-index 속성 값
   */
  zIndex?: number

  /**
   * 마커 투명도 (0-1)
   */
  opacity?: number

  /**
   * 로드뷰에 올라있는 마커의 높이 값(m 단위)
   */
  altitude?: number

  /**
   * 로드뷰 상에서 마커의 가시반경(m 단위), 두 지점 사이의 거리가 지정한 값보다 멀어지면 마커는 로드뷰에서 보이지 않게 된다.
   */
  range?: number

  /**
   * InfoWindow 옵션
   */
  infoWindowOptions?: {
    /**
     * 인포윈도우를 열 때 지도가 자동으로 패닝하지 않을지의 여부 (기본값: false)
     */
    disableAutoPan?: boolean

    /**
     * 삭제 가능한 인포윈도우
     */
    removable?: boolean

    /**
     * 인포윈도우 엘리먼트의 z-index 속성 값
     */
    zIndex?: number
  }
}

/**
 * Map에서 Marker를 생성할 때 사용 합니다.
 * `onCreate` 이벤트를 통해 생성 후 `maker` 객체에 직접 접근하여 초기 설정이 가능합니다.
 */
const RoadviewMarker = React.forwardRef<
  kakao.maps.Marker,
  React.PropsWithChildren<RoadviewMarkerProps>
>(
  (
    {
      image,
      position,
      children,
      altitude,
      clickable,
      infoWindowOptions,
      onClick,
      onDragEnd,
      onDragStart,
      onMouseOut,
      onMouseOver,
      onCreate,
      opacity,
      range,
      title,
      zIndex,
    },
    ref
  ) => {
    const roadview = useRoadview(`RoadviewMarker`)

    const markerImage = useMemo(() => {
      return (
        image &&
        new kakao.maps.MarkerImage(
          image.src,
          new kakao.maps.Size(image.size.width, image.size.height),
          {
            alt: image.options?.alt,
            coords: image.options?.coords,
            offset:
              image.options?.offset &&
              new kakao.maps.Point(
                image.options?.offset.x,
                image.options?.offset.y
              ),
            shape: image.options?.shape,
            spriteOrigin:
              image.options?.spriteOrigin &&
              new kakao.maps.Point(
                image.options?.spriteOrigin.x,
                image.options?.spriteOrigin.y
              ),
            spriteSize:
              image.options?.spriteSize &&
              new kakao.maps.Size(
                image.options?.spriteSize.width,
                image.options?.spriteSize.height
              ),
          }
        )
      )
    }, [image])

    const markerPosition = useMemo(() => {
      if ("lat" in position) {
        return new kakao.maps.LatLng(position.lat, position.lng)
      }
      if ("x" in position) {
        return new kakao.maps.Coords(position.x, position.y).toLatLng()
      }
      return new kakao.maps.Viewpoint(
        position.pan,
        position.tilt,
        position.zoom,
        position.panoId
      )

      /* eslint-disable react-hooks/exhaustive-deps */
    }, [
      // @ts-ignore
      position.lat,
      // @ts-ignore
      position.lng,
      // @ts-ignore
      position.x,
      // @ts-ignore
      position.y,
      // @ts-ignore
      position.pan,
      // @ts-ignore
      position.tilt,
      // @ts-ignore
      position.zoom,
      // @ts-ignore
      position?.panoId,
    ])
    /* eslint-enable react-hooks/exhaustive-deps */

    return (
      <Marker
        map={roadview}
        position={markerPosition}
        image={markerImage}
        altitude={altitude}
        clickable={clickable}
        infoWindowOptions={infoWindowOptions}
        onClick={onClick}
        onDragEnd={onDragEnd}
        onDragStart={onDragStart}
        onMouseOut={onMouseOut}
        onMouseOver={onMouseOver}
        onCreate={onCreate}
        opacity={opacity}
        range={range}
        title={title}
        zIndex={zIndex}
        ref={ref}
      >
        {children}
      </Marker>
    )
  }
)

export default RoadviewMarker
