import React, {
  ReactNode,
  Ref,
  useImperativeHandle,
  useLayoutEffect,
  useMemo,
} from "react"
import useMap from "../hooks/useMap"

export const DrawingManagerContext =
  React.createContext<kakao.maps.drawing.DrawingManager>(
    undefined as unknown as kakao.maps.drawing.DrawingManager
  )

export type DrawingManagerProps<
  T extends kakao.maps.drawing.OverlayType = kakao.maps.drawing.OverlayType
> = Partial<kakao.maps.drawing.OverlayOptions> &
  Pick<kakao.maps.drawing.OverlayOptions, `${T}Options`> & {
    /**
     * 마우스 오버 시 가이드 툴팁 표시 여부. ‘draw’, ‘drag’, ‘edit’ 3가지를 지정할 수 있다 (기본값: 모두 표시 안함)
     * 예를들어 [‘draw’]로 설정하면 객체를 그릴때 가이드 툴팁이 표시된다
     */
    guideTooltip?: Array<"draw" | "drag" | "edit">
    /**
     * 사용할 그리기 요소 지정한다 (기본값: 모든 그리기 요소)
     */
    drawingMode?: Array<T>

    /**
     * 그리기 요소를 선택하면 발생한다.
     */
    onSelect?: (
      drawingManager: kakao.maps.drawing.DrawingManager<T>,
      event: kakao.maps.drawing.MouseEvent
    ) => void

    /**
     * 그리기를 시작하면 발생한다.
     */
    onDrawstart?: (
      drawingManager: kakao.maps.drawing.DrawingManager<T>,
      event: kakao.maps.drawing.MouseEvent
    ) => void

    /**
     * 그리기 시작 후, 마우스를 이동하면 발생한다.
     */
    onDraw?: (
      drawingManager: kakao.maps.drawing.DrawingManager<T>,
      event: kakao.maps.drawing.MouseEvent
    ) => void

    /**
     * 그리기를 시작하면 발생한다.
     */
    onDrawend?: (
      drawingManager: kakao.maps.drawing.DrawingManager<T>,
      event: kakao.maps.drawing.MouseEvent
    ) => void

    /**
     * 다음 단계 그리기를 하면 발생한다. (Polyline, Polygon, Arrow 한정)
     */
    onDrawnext?: (
      drawingManager: kakao.maps.drawing.DrawingManager<T>,
      event: kakao.maps.drawing.MouseEvent
    ) => void

    /**
     * 그리기를 취소하면 발생한다.
     */
    onCancle?: (
      drawingManager: kakao.maps.drawing.DrawingManager<T>,
      event: kakao.maps.drawing.MouseEvent
    ) => void

    /**
     * 그리기 요소를 삭제하면 발생한다.
     */
    onRemove?: (
      drawingManager: kakao.maps.drawing.DrawingManager<T>,
      event: kakao.maps.drawing.MouseEvent
    ) => void

    /**
     * 그리기 요소들의 상태가 변경되면 발생한다.
     * 각 요소의 생성/수정/이동/삭제 동작과 undo 또는 redo 함수 호출이 이에 해당한다.
     */
    onStateChanged?: (
      drawingManager: kakao.maps.drawing.DrawingManager<T>
    ) => void

    /**
     * 객체 생성후 해당 이벤트가 발생합니다.
     */
    onCreate?: (drawingManager: kakao.maps.drawing.DrawingManager<T>) => void

    /**
     * Toolbox에 대해서 추가할 때 사용합니다.
     */
    children?: ReactNode
  }

function useDrawingManagerEvent<T extends kakao.maps.drawing.OverlayType>(
  target: kakao.maps.drawing.DrawingManager<T> | undefined,
  type:
    | "drawstart"
    | "draw"
    | "drawend"
    | "drawnext"
    | "select"
    | "cancel"
    | "remove"
    | "state_changed",
  callback:
    | ((target: kakao.maps.drawing.DrawingManager<T>, ...args: any) => void)
    | undefined
) {
  useLayoutEffect(() => {
    if (!target || !callback) return

    const wrapCallback = (...arg: any[]) => {
      if (arg === undefined) return callback(target)
      else return callback(target, ...arg)
    }

    // @ts-ignore
    target.addListener(type, wrapCallback)
  }, [callback, target, type])
}

/**
 * 그리기 관리자 객체를 생성하는 컴포넌트 입니다.
 *
 * 초기 생성자에 필요한 Props는 최초 렌더링에만 반영을 하고 이후에는 값이 변경되더라도 적용되지 않습니다.
 *
 * > `on~` 시리즈를 제외한 props는 초기 렌더링 이후 작동 안함
 *
 * DrawingManager는 이전 Map, Marker, CustomOverlay와 달리 Props를 통해 제어를 하는 것이 아닌 직접 DrawingManager 객체를 이용하여 제어를 해야 합니다.
 *
 * 이를 위해 ref 객체를 통해 `DrawingManager` 객체를 접근 할 수 있으며, 이를 활용하여 여러 이벤트 처리 및 제어가 가능합니다.
 *
 * TypeScript 사용자를 위한 `Generic`이 적용되어 있으므로, `ref` 객체에 대한 typing 및 `drawingMode`에 대해 확실하게 정의해야 합니다.
 *
 * ```tsx
 * const Main = () => {
 *     const managerRef = useRef<kakao.maps.drawing.DrawingManager<
 *       kakao.maps.drawing.OverlayType.POLYLINE
 *     >>(null);
 *
 *     function selectOverlay(type: string) {
 *       const manager = managerRef.current;
 *       manager.cancel();
 *       manager.select(kakao.maps.drawing.OverlayType.POLYLINE);
 *     }
 *
 *     return (
 *       <>
 *         <Map
 *           center={{
 *             // 지도의 중심좌표
 *             lat: 33.450701,
 *             lng: 126.570667,
 *           }}
 *           style={{
 *             width: "100%",
 *             height: "450px",
 *           }}
 *           level={3} // 지도의 확대 레벨
 *         >
 *           <DrawingManager
 *             ref={managerRef}
 *             drawingMode={[
 *               kakao.maps.drawing.OverlayType.POLYLINE,
 *             ]}
 *             guideTooltip={['draw', 'drag', 'edit']}
 *             polylineOptions={{
 *               draggable: true,
 *               removable: true,
 *               editable: true
 *             }}
 *           />
 *         </Map>
 *         <button onClick={(e) => {
 *           selectOverlay('POLYLINE')
 *         }}>선</button>
 *       </>
 *     )
 *   }
 * ```
 *
 * > JavaScript 코드 버전
 *
 * ```jsx live
 * function() {
 *   const Main = () => {
 *     const managerRef = useRef(null);
 *
 *     function selectOverlay() {
 *       const manager = managerRef.current;
 *       manager.cancel();
 *       manager.select(kakao.maps.drawing.OverlayType.POLYLINE);
 *     }
 *
 *     return (
 *       <>
 *         <Map
 *           center={{
 *             // 지도의 중심좌표
 *             lat: 33.450701,
 *             lng: 126.570667,
 *           }}
 *           style={{
 *             width: "100%",
 *             height: "450px",
 *           }}
 *           level={3} // 지도의 확대 레벨
 *         >
 *           <DrawingManager
 *             ref={managerRef}
 *             drawingMode={[
 *               kakao.maps.drawing.OverlayType.POLYLINE,
 *             ]}
 *             guideTooltip={['draw', 'drag', 'edit']}
 *             polylineOptions={{
 *               draggable: true,
 *               removable: true,
 *               editable: true
 *             }}
 *           />
 *         </Map>
 *         <button onClick={selectOverlay}>선</button>
 *       </>
 *     )
 *   }
 *   return (<Main />)
 * }
 * ```
 */
const DrawingManager = React.forwardRef(function <
  T extends kakao.maps.drawing.OverlayType
>(
  {
    arrowOptions,
    circleOptions,
    ellipseOptions,
    markerOptions,
    polygonOptions,
    polylineOptions,
    rectangleOptions,
    drawingMode,
    guideTooltip,
    onSelect,
    onDrawstart,
    onDraw,
    onDrawend,
    onDrawnext,
    onCancle,
    onRemove,
    onStateChanged,
    onCreate,
    children,
  }: DrawingManagerProps<T>,
  ref: Ref<kakao.maps.drawing.DrawingManager<T>>
) {
  const map = useMap("Toolbox")

  const drawingManager = useMemo(
    () => {
      if (!window.kakao.maps.drawing) {
        console.warn(
          "drawing 라이브러리를 별도 로드 해야 사용 가능합니다. `https://apis.map.kakao.com/web/guide/#loadlibrary`"
        )
        return
      }

      return new kakao.maps.drawing.DrawingManager({
        // @ts-ignore
        map,
        drawingMode,
        guideTooltip,
        arrowOptions,
        circleOptions,
        ellipseOptions,
        markerOptions,
        polygonOptions,
        polylineOptions,
        rectangleOptions,
      })
    },
    // eslint-disable-next-line react-hooks/exhaustive-deps
    []
  )
  useImperativeHandle(ref, () => drawingManager!, [drawingManager])

  useLayoutEffect(() => {
    drawingManager && onCreate && onCreate(drawingManager)
  }, [drawingManager, onCreate])

  useDrawingManagerEvent(drawingManager, "select", onSelect)
  useDrawingManagerEvent(drawingManager, "drawstart", onDrawstart)
  useDrawingManagerEvent(drawingManager, "draw", onDraw)
  useDrawingManagerEvent(drawingManager, "drawend", onDrawend)
  useDrawingManagerEvent(drawingManager, "drawnext", onDrawnext)
  useDrawingManagerEvent(drawingManager, "cancel", onCancle)
  useDrawingManagerEvent(drawingManager, "remove", onRemove)
  useDrawingManagerEvent(drawingManager, "state_changed", onStateChanged)

  if (!drawingManager) return null

  return (
    <DrawingManagerContext.Provider value={drawingManager}>
      {children}
    </DrawingManagerContext.Provider>
  )
}) as unknown as <
  T extends kakao.maps.drawing.OverlayType = kakao.maps.drawing.OverlayType
>(
  _props: DrawingManagerProps<T> &
    React.RefAttributes<kakao.maps.drawing.DrawingManager<T>>
) => null

export default DrawingManager
import React, {
  Ref,
  useContext,
  useImperativeHandle,
  useLayoutEffect,
  useMemo,
} from "react"
import useMap from "../hooks/useMap"
import { DrawingManagerContext } from "./DrawingManager"

export type ToolboxProps = {
  /**
   * Toolbox의 Position를 정의 한다.
   */
  position?: kakao.maps.ControlPosition
}

/**
 * 그리기 툴박스를 생성합니다.
 *
 * 해당 컴포넌트는 반드시 `DrawingManager` 컴포넌트의 자식으로 존재해야 합니다.
 */
const Toolbox = React.forwardRef(function (
  { position }: ToolboxProps,
  ref: Ref<kakao.maps.drawing.Toolbox>
) {
  position = position || kakao.maps.ControlPosition.TOP

  const map = useMap("Toolbox")
  const drawingmanager = useContext(DrawingManagerContext)

  if (!drawingmanager) {
    throw new Error("Toolbox must exist inside DrawingManager Component!`")
  }

  const toolbox = useMemo(
    () =>
      new kakao.maps.drawing.Toolbox({
        drawingManager: drawingmanager,
      }),
    [drawingmanager]
  )
  useImperativeHandle(ref, () => toolbox, [toolbox])

  useLayoutEffect(() => {
    const element = toolbox.getElement()
    map.addControl(element, position!)
    return () => {
      map.removeControl(element)
    }
  }, [map, toolbox, position])

  return null
})

export default Toolbox
import React, { useRef, useState, useImperativeHandle } from "react"
import useIsomorphicLayoutEffect from "../hooks/useIsomorphicLayoutEffect"

export interface StaticMapProps {
  /**
   * MapContinaer의 id에 대해서 지정합니다.
   */
  id?: string

  /**
   * MapContainer의 className에 대해서 지정합니다.
   */
  className?: string

  /**
   * MapContainer의 style에 대해서 지정합니다.
   */
  style?: React.CSSProperties

  /**
   * 중심으로 설정할 위치 입니다.
   */
  center: {
    lat: number
    lng: number
  }

  /**
   * 확대 수준
   * @default 3
   */
  level?: number

  /**
   * 지도 종류 (기본값: 일반 지도)
   */
  mapTypeId?: kakao.maps.MapTypeId

  /**
   * 이미지 지도에 표시할 마커 또는 마커 배열
   */
  marker:
    | boolean
    | {
        /**
         * 마커 tooltip에 표시될 내용
         */
        text?: string
        /**
         * 마커 포지션
         */
        position?: {
          lat: number
          lng: number
        }
      }
    | Array<{
        /**
         * 마커 tooltip에 표시될 내용
         */
        text?: string
        /**
         * 마커 포지션
         */
        position: {
          lat: number
          lng: number
        }
      }>
  /**
   * StaticMap 생성 이벤트 핸들러
   */
  onCreate?: (maker: kakao.maps.StaticMap) => void
}

const StaticMap = React.forwardRef<kakao.maps.StaticMap, StaticMapProps>(
  (
    {
      id = "react-kakao-maps-sdk-staticmap-container",
      style,
      className,
      center,
      marker,
      level,
      mapTypeId,
      onCreate,
    },
    ref
  ) => {
    const [map, setMap] = useState<kakao.maps.StaticMap>()
    const container = useRef<HTMLDivElement>(null)

    useIsomorphicLayoutEffect(() => {
      if (!window.kakao) {
        console.warn(
          "kakao map javascript api를 먼저 불러와야 합니다. `https://apis.map.kakao.com/web/guide`"
        )
        return
      }

      const mapContainer = container.current
      if (!mapContainer) return

      kakao.maps.load(() => {
        const _marker = (() => {
          if (Array.isArray(marker)) {
            return marker.map((mk) => {
              return {
                ...mk,
                position: new kakao.maps.LatLng(
                  mk.position.lat,
                  mk.position.lng
                ),
              }
            })
          }
          if (typeof marker === "object") {
            if (!!marker.position) {
              return {
                ...marker,
                position: new kakao.maps.LatLng(
                  marker.position.lat,
                  marker.position.lng
                ),
              }
            }
            return marker as { text: string }
          }
          return marker
        })()
        const kakaoStaticMap = new kakao.maps.StaticMap(mapContainer, {
          center: new kakao.maps.LatLng(center.lat, center.lng),
          level,
          mapTypeId,
          marker: _marker,
        })

        setMap(kakaoStaticMap)
      })
    }, [JSON.stringify(marker)])

    useImperativeHandle(ref, () => map!, [map])

    useIsomorphicLayoutEffect(() => {
      if (map) map.setCenter(new kakao.maps.LatLng(center.lat, center.lng))
    }, [map, center.lat, center.lng])

    useIsomorphicLayoutEffect(() => {
      if (map && level) map.setLevel(level)
    }, [map, level])

    useIsomorphicLayoutEffect(() => {
      if (map && mapTypeId) map.setMapTypeId(mapTypeId)
    }, [map, mapTypeId])

    useIsomorphicLayoutEffect(() => {
      if (map && onCreate) onCreate(map)
    }, [map, onCreate])

    return (
      <div id={id} style={style} className={className} ref={container}></div>
    )
  }
)

export default StaticMap
import { useEffect, useMemo, useState } from "react"
import { Loader, LoaderOptions } from "../util/kakaoMapApiLoader"

/**
 * Kakao Map Api를 injection 합니다.
 *
 * 해당 hook은 cleanup 시점에도 Kakao Map Api를 제거 하지 않고, 동일한 hook를 여러 위치에서 호출 하더라도 최초 한번만 injection 됩니다.
 */
const useInjectKakaoMapApi = (options: LoaderOptions) => {
  const [state, setState] = useState<{
    loading: boolean
    error: ErrorEvent | undefined
  }>({
    loading: true,
    error: undefined,
  })

  const loader = useMemo(
    () => new Loader(options),
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [JSON.stringify(options)]
  )

  useEffect(() => {
    loader
      .load()
      .then(() =>
        setState({
          loading: false,
          error: undefined,
        })
      )
      .catch((error) =>
        setState({
          loading: false,
          error: error,
        })
      )
  }, [loader])

  return state
}

export default useInjectKakaoMapApi
