import MDBox from "components/MDBox";

import Footer from "examples/Footer";
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import data from "layouts/drone/data/authorsTableData";

const API_KEY = "11f51a135860cbff7041b080fabf611f";

function Weather() {
  function onGeoOk(position) {
    const { latitude, longitude } = position.coords;

    console.log(`You live in ${latitude} and ${longitude}`);

    fetch(
      `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=${API_KEY}`
    )
      .then((response) => response.json())
      .then((data) =>
        console.log(`온도 : ${data.main.temp - 273.15}, 날씨 : ${data.weather[0].main}`)
      );
  }

  function onGeoError() {
    alert("Can't find you. No weather for you.");
  }

  navigator.geolocation.getCurrentPosition(onGeoOk, onGeoError);

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <MDBox>온도 :</MDBox>
      <Footer />
    </DashboardLayout>
  );
}

export default Weather;
