import axios from "axios";
import MDBox from "components/MDBox";
import Footer from "examples/Footer";
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import { useEffect, useState } from "react";

const API_KEY = "11f51a135860cbff7041b080fabf611f";

function Weather() {
  const [lat, setLat] = useState("Loading...");
  const [long, setLong] = useState("Loading...");
  const [data, setData] = useState("");
  const [temp, setTemp] = useState("Loading...");
  const [weather, setWeather] = useState("Loading...");

  function onGeoOk(position) {
    setLat(position.coords.latitude);
    setLong(position.coords.longitude);

    useEffect(async () => {
      const result = await axios(
        `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${long}&appid=${API_KEY}`
      );
      setData(result.data);
    }, []);
    setTemp(result.data.main.temp);
    setWeather(result.data.weather[0].main);
    // fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${long}&appid=${API_KEY}`)
    //   .then((response) => response.json())
    //   .then(
    //     (data) => console.log(`온도 : ${data.main.temp - 273.15}, 날씨 : ${data.weather[0].main}`),
    //     (data) => setTemp(${data.main.temp - 273.15}),
    //     (data) => setWeather(data.weather[0].main)
    //   );
  }

  function onGeoError() {
    alert("Can't find you. No weather for you.");
  }
  console.log(`You live in ${lat} and ${long}`);
  console.log(`${temp} and ${weather}`);

  navigator.geolocation.getCurrentPosition(onGeoOk, onGeoError);

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <MDBox> 위도 : {lat}</MDBox>
      <MDBox> 경도 : {long}</MDBox>
      <MDBox> 온도 : {temp}</MDBox>
      <MDBox> 날씨 : {weather}</MDBox>
      <Footer />
    </DashboardLayout>
  );
}

export default Weather;
