import { Grid } from "@mui/material";

import Footer from "examples/Footer";
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import { useState } from "react";

const API_KEY = "11f51a135860cbff7041b080fabf611f";

function Weather() {
  function onGeoOk(position) {
    const [lat, setLat] = useState("");
    const [long, setLong] = useState("");

    setLat(position.coords);

    console.log(`You live in ${lat} and ${long}`);

    fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${long}&appid=${API_KEY}`)
      .then((response) => response.json())
      .then((data) =>
        console.log(`온도 : ${data.main.temp - 273.15}, 날씨 : ${data.weather[0].main}`)
      );
  }

  function onGeoError() {
    alert("Can't find you. No weather for you.");
  }

  navigator.geolocation.getCurrentPosition(onGeoOk, onGeoError);

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <Grid>
        <div className="appContentWrap">{setLat}</div>
      </Grid>
      <Footer />
    </DashboardLayout>
  );
}

export default Weather;
